<template>
  <div class="script-page">
    <div class="page-header">
      <el-page-header @back="goBack">
        <template #content>
          <span class="page-title">{{ scriptName }}</span>
        </template>
        <template #extra>
          <el-button type="primary" plain @click="refreshData">
            <el-icon><Refresh /></el-icon>
            刷新
          </el-button>
        </template>
      </el-page-header>
    </div>

    <div class="page-content">
      <!-- 根据脚本 ID 动态加载组件 -->
      <NuxtPage />
    </div>
  </div>
</template>

<script setup lang="ts">
import { Refresh } from '@element-plus/icons-vue'

const route = useRoute()
const router = useRouter()

// 从路由参数获取脚本 ID
const scriptId = computed(() => route.params.id as string)

// 脚本名称映射
const scriptNames: Record<string, string> = {
  'url-expander': 'URL 短链接扩展器',
  'data-processor': '数据处理器',
  'batch-export': '批量导出工具',
  'custom-script': '自定义脚本'
}

const scriptName = computed(() => scriptNames[scriptId.value] || '脚本执行')

function goBack() {
  router.push('/')
}

function refreshData() {
  // 刷新逻辑由具体页面实现
  window.location.reload()
}
</script>

<style scoped>
.script-page {
  max-width: 1200px;
  margin: 0 auto;
}

.page-header {
  margin-bottom: 24px;
  background: white;
  padding: 20px;
  border-radius: 8px;
  box-shadow: 0 2px 12px rgba(0, 0, 0, 0.05);
}

.page-title {
  font-size: 18px;
  font-weight: 600;
  color: #303133;
}

.page-content {
  background: white;
  border-radius: 8px;
  padding: 24px;
  min-height: 400px;
  box-shadow: 0 2px 12px rgba(0, 0, 0, 0.05);
}
</style>
